package pkg1;
//pgm1
public class unarymodify {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=12,b,c;
//a value after1q12`postincrement
System.out.println("a="+(a++));
// a value after preincrement
System.out.println("b="+(++a));
//a value after postdecrement
System.out.println("c="+(a--));
//a value after predecrement
System.out.println("d="+(--a));
	}

}
